import{priorityList}from'../schedular';function scrollTo(a){let b=this.apiInstance;b.addJob('scrollToAPoint',()=>{if(0<=a&&1>=a){let c=b.getChildren&&b.getChildren(),d=c.xAxis[0],e=d.getLimit&&d.getLimit(),f=d.getVisibleConfig&&d.getVisibleConfig(),g=f.maxValue-f.minValue,h=e.max-e.min,i=a*(h-g)+e.min,j=(f.minValue-e.min)/(h-g);b.fireChartInstanceEvent('scrollStart',{scrollPosition:j}),d.setVisibleConfig(i,i+g),b.fireChartInstanceEvent('scrollEnd',{previousScrollPosition:j,scrollPosition:a})}},priorityList.postRender)}export{scrollTo};